#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>

#define MAX_LEN_EACH_STRING 1100

void MyStringCat(char* arr1, const char* arr2);

int main(void)
{
	char* arr1 = malloc(2 * MAX_LEN_EACH_STRING * sizeof(char));
	char* arr2 = malloc(MAX_LEN_EACH_STRING * sizeof(char));

	int num_of_line = 0;
	scanf("%d", &num_of_line);

	for (int i = 0; i < num_of_line; i++)
	{
		memset(arr1, 0, 2 * MAX_LEN_EACH_STRING * sizeof(char));
		memset(arr2, 0, MAX_LEN_EACH_STRING * sizeof(char));

		scanf("%1100s%1100s", arr1, arr2);
		MyStringCat(arr1, arr2);
		printf("%s\n", arr1);
	}

	free(arr1);
	free(arr2);

	return 0;
}

void MyStringCat(char* arr1, const char* arr2)
{
	char first_ch_of_arr2 = *arr2;
	int len_of_arr1 = (int)strlen(arr1);
	int len_of_arr2 = (int)strlen(arr2);

	int num_of_same_ch = 0;
	int* array_of_same_ch = malloc(MAX_LEN_EACH_STRING * sizeof(int));

	for (int i = len_of_arr1 - 1; i >= 0 && i >= len_of_arr1 - len_of_arr2; i--)
	{
		if (*(arr1 + i) == first_ch_of_arr2)
		{
			*(array_of_same_ch + num_of_same_ch) = i;
			num_of_same_ch++;
		}
	}

	bool is_copy_done = false;
	for (int i = num_of_same_ch - 1; i >= 0; i--)
	{
		if (strncmp(arr1 + *(array_of_same_ch + i), arr2,
			len_of_arr1 - *(array_of_same_ch + i)) == 0)
		{
			strncpy(arr1 + *(array_of_same_ch + i), arr2,
				len_of_arr2 + 1);
			is_copy_done = true;
			break;
		}
	}

	if (is_copy_done == false)
		strncpy(arr1 + len_of_arr1, arr2, len_of_arr2 + 1);

	free(array_of_same_ch);
}

